mapper.insert:
INSERT INTO 
chatroom(pr_id, sellerId, buyerId, createdDate, sellerName, buyerName, pr_title, chatReadBuy, chatReadSell)
VALUES (#{pr_id}, #{sellerId}, #{buyerId}, #{createdDate}, #{sellerName}, #{buyerName}, #{pr_title}, 1, 1);

mapper.select:
 	SELECT *
 	FROM chatroom AS c
 	JOIN product as p
 	ON c.pr_id = p.pr_id
 	WHERE c.sellerid = #{email}
		OR c.buyerid = #{email};

mapper.update:
UPDATE chatroom
SET FILENAME = #{fileName}
WHERE ID = #{id};

UPDATE chatroom
SET CHATREADBUY = #{chatReadBuy}
WHERE ID = #{id};

UPDATE chatroom
SET CHATREADSELL = #{chatReadSell}
WHERE ID = #{id};

UPDATE chatroom
SET CHATMESSAGE = #{chatMessage}
WHERE ID =#{id};

mapper.select:
SELECT chatMessage
FROM chatroom
WHERE ID = #{id};
